section.script:
document.addEventListener('DOMContentLoaded', function() {
    const serviceButtons = document.querySelectorAll('.service-type-btn');
    const selectedServiceInput = document.getElementById('selectedServiceType');

    // Estilos para botón activo e inactivo
    const activeStyle = {
        background: 'linear-gradient(135deg, rgba(227, 30, 36, 0.85) 0%, rgba(201, 25, 32, 0.85) 100%)',
        border: '1px solid rgba(227, 30, 36, 0.8)',
        boxShadow: '0 6px 20px rgba(227, 30, 36, 0.4)',
        transform: 'scale(1.02)'
    };

    const inactiveStyle = {
        background: 'rgba(255, 255, 255, 0.08)',
        border: '1px solid rgba(255, 255, 255, 0.25)',
        boxShadow: '0 3px 12px rgba(0, 0, 0, 0.15)',
        transform: 'scale(1)'
    };

    serviceButtons.forEach(button => {
        button.addEventListener('click', function() {
            // Remover clase active de todos los botones
            serviceButtons.forEach(btn => {
                btn.classList.remove('active');
                // Aplicar estilos inactivos
                btn.style.background = inactiveStyle.background;
                btn.style.border = inactiveStyle.border;
                btn.style.boxShadow = inactiveStyle.boxShadow;
                btn.style.transform = inactiveStyle.transform;
            });

            // Agregar clase active al botón clickeado
            this.classList.add('active');
            // Aplicar estilos activos
            this.style.background = activeStyle.background;
            this.style.border = activeStyle.border;
            this.style.boxShadow = activeStyle.boxShadow;
            this.style.transform = activeStyle.transform;

            // Actualizar el valor del input hidden
            const serviceType = this.getAttribute('data-service');
            selectedServiceInput.value = serviceType;

            // Opcional: console log para debugging
            console.log('Servicio seleccionado:', serviceType);
        });
    });
});
